@mixin buttonBackground($btn-variant, $background) {
  $background-dark: darken($background, 10%);
  $background-light: lighten($background, 10%);

  .btn-#{$btn-variant} {
    color: white;
    font-weight: 500;
    background: $background;
    &:disabled {
      cursor: not-allowed;
      background-color: lighten($background, 30%);
